import React, {useEffect, useState} from "react";
import Typography from "@material-ui/core/Typography";
import PropTypes from 'prop-types';
import { makeStyles } from '@material-ui/core/styles';
import Box from '@material-ui/core/Box';
import Collapse from '@material-ui/core/Collapse';
import IconButton from '@material-ui/core/IconButton';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import KeyboardArrowDownIcon from '@material-ui/icons/KeyboardArrowDown';
import KeyboardArrowUpIcon from '@material-ui/icons/KeyboardArrowUp';
import $ from "jquery";
import Button from "@material-ui/core/Button";
import Container from "@material-ui/core/Container";
import CssBaseline from "@material-ui/core/CssBaseline";
import Grid from "@material-ui/core/Grid";
import TextField from "@material-ui/core/TextField";
import FormControl from "@material-ui/core/FormControl";
import InputLabel from "@material-ui/core/InputLabel";
import Select from "@material-ui/core/Select";
import Modal from "@material-ui/core/Modal";
import Backdrop from "@material-ui/core/Backdrop";
import Fade from "@material-ui/core/Fade";
const useStyles = makeStyles((theme) => ({
    modal: {
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
    },
    paper: {
        backgroundColor: theme.palette.background.paper,
        border: '2px solid #000',
        boxShadow: theme.shadows[5],
        padding: theme.spacing(2, 4, 3),
    },
}));
const useStylesForm = makeStyles((theme) => ({
    paper: {
        marginTop: theme.spacing(8),
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
    },
    avatar: {
        margin: theme.spacing(1),
        backgroundColor: theme.palette.secondary.main,
    },
    form: {
        width: '100%', // Fix IE 11 issue.
        marginTop: theme.spacing(3),
    },
    submit: {
        margin: theme.spacing(3, 0, 2),
    },
    margin: {
        margin: theme.spacing(1),
    }
}));
function AddRoom(props) {
    const classes = useStylesForm();
    const house_id = props.house_id;
    const room_types = ['','KITCHEN','BEDROOM','BATHROOM','LIVING'];
    const [state,setState] = useState({
        square: 0,
        height: 0,
        room_type:''
    })
    const handleChange = (event) => {
        const name = event.target.name;
        setState({
            ...state,
            [name]: event.target.value,
        });
    };

    function sendRoom(square,height,room_type,house_id){
        $.ajax({
                url: "api/addroom",
                method:"POST",
                data:{
                    height:height,
                    square:square,
                    room_type:room_type,
                    user_id:window.sessionStorage.getItem("id"),
                    house_id:house_id
                },
                async:false,
                success:function (res){
                    if(res.success){
                        alert('success')
                    }else {
                        //dispatch(clearAccount());
                        alert(res.message);
                    }
                }
            }
        );
    }


    return (
        <Container component="main" maxWidth="xs">
            <CssBaseline />
            <div className={classes.paper}>
                <Typography component="h1" variant="h5">
                    Add House
                </Typography>
                <form className={classes.form} noValidate>
                    <Grid container spacing={2}>
                        <Grid item xs={12}>
                            <TextField
                                onChange={handleChange}
                                variant="outlined"
                                required
                                fullWidth
                                name="square"
                                label="square"
                                id="square"
                                autoComplete="current-square"
                                type={"number"}
                            />
                        </Grid>
                        <Grid item xs={12}>
                            <TextField
                                onChange={handleChange}
                                variant="outlined"
                                required
                                fullWidth
                                name="height"
                                label="height"
                                id="height"
                                autoComplete="current-height"
                                type={"number"}
                            />
                        </Grid>
                        <FormControl className={classes.margin}>
                            <InputLabel id="demo-customized-select-label">Room type</InputLabel>
                            <Select
                                native
                                value={state.room_type}
                                onChange={handleChange}
                                inputProps={{
                                    name: 'room_type',
                                    id: 'type-native-simple',
                                }}
                            >
                                {
                                    room_types.map(item=>(
                                        <option value={item}>{item}</option>
                                    ))
                                }
                            </Select>
                        </FormControl>
                    </Grid>
                    <Button
                        type="submit"
                        fullWidth
                        variant="contained"
                        color="primary"
                        className={classes.submit}
                        onClick={()=>sendRoom(state.square,state.height,state.room_type,house_id)}
                    >
                        Add Room
                    </Button>
                </form>
            </div>
        </Container>
    );
}

const useRowStyles = makeStyles({
    root: {
        '& > *': {
            borderBottom: 'unset',
        },
    },

});

function AddFurniture(props) {
    const classes = useStylesForm();
    const roomId = props.room_id;
    const furniture_type = ['','AIR_CONDITION','LIGHT', 'HUMIDIFIER', 'BATHTUB', 'OUTLET','CURTAINS', 'FAN', 'CAMERA', 'WATER_HEATER'];
    const [state,setState] = useState({
        manufacture: 0,
        furniture_type:''
    })
    const handleChange = (event) => {
        const name = event.target.name;
        setState({
            ...state,
            [name]: event.target.value,
        });
    };

    function sendFurniture(manufacture,furniture_type){
        $.ajax({
                url: "api/addfurniture",
                method:"POST",
                data:{
                    manufacture: manufacture,
                    furniture_type:furniture_type,
                    room_id:roomId
                },
                async:false,
                success:function (res){
                    if(res){
                        alert('success')
                    }
                }
            }
        );
    }


    return (
        <Container component="main" maxWidth="xs">
            <CssBaseline />
            <div className={classes.paper}>
                <Typography component="h1" variant="h5">
                    Add Furniture
                </Typography>
                <form className={classes.form} noValidate>
                    <Grid container spacing={2}>
                        <Grid item xs={12}>
                            <TextField
                                onChange={handleChange}
                                variant="outlined"
                                required
                                fullWidth
                                name="manufacture"
                                label="Manufacture"
                                id="manufacture"
                                autoComplete="current-manufacture"
                            />
                        </Grid>
                        <FormControl className={classes.margin}>
                            <InputLabel id="demo-customized-select-label">Room type</InputLabel>
                            <Select
                                native
                                value={state.furniture_type}
                                onChange={handleChange}
                                inputProps={{
                                    name: 'furniture_type',
                                    id: 'type-native-simple',
                                }}
                            >
                                {
                                    furniture_type.map(item=>(
                                        <option value={item}>{item}</option>
                                    ))
                                }
                            </Select>
                        </FormControl>
                    </Grid>
                    <Button
                        type="submit"
                        fullWidth
                        variant="contained"
                        color="primary"
                        className={classes.submit}
                        onClick={()=>sendFurniture(state.manufacture,state.furniture_type)}
                    >
                        Add furniture
                    </Button>
                </form>
            </div>
        </Container>
    );
}
function Furniture(props){
    const {furniture} = props;
    const classes = useRowStyles();

    return (
        <React.Fragment>
            <TableRow className={classes.root}>
                <TableCell component="th" scope="row">
                    {furniture.id}
                </TableCell>
                <TableCell align="right">{furniture.furniture_type}</TableCell>
                <TableCell align="right">{furniture.manufacture}</TableCell>
                <TableCell align="right">{furniture.available}</TableCell>
            </TableRow>
        </React.Fragment>
    );
}

function Room(props){
    const {room} = props;
    const classes = useRowStyles();
    const formclasses =useStyles();
    const [open, setOpen] = React.useState(false);
    const [formopen,setFormopen] = React.useState(false);
    const [funirtureaquired,setFunirtureaquired] = React.useState(false);
    function getFurniture(){
        $.ajax({
                url: "api/furniture",
                method:"POST",
                headers: {
                    'Content-Type': 'application/json', // 设置内容类型为 JSON
                    'token': window.sessionStorage.getItem("token") // 设置授权头
                    // 可以添加其他自定义请求头
                },
                async:false,
                success:function (res){
                    if(res.length > 0){
                        room.furniture.length = 0;
                        for(var i in res){
                            if(res[i].available) {
                                room.furniture.push({
                                    available:"Yes",
                                    furniture_type:res[i].furniture_type,
                                    manufacture:res[i].manufacture,
                                    id:res[i].id
                                })
                            }else{
