    const formclasses =useStyles();
    function getRoom(){
        $.ajax({
                url: "api/getroom",
                method:"POST",
                data:{
                    house_id: row.id
                },
                async:false,
                success:function (res){
                    if(res.length > 0){
                        row.rooms.length = 0;
                        for(var i in res){
                            if(res[i].is_filled) {
                                row.rooms.push({
                                    id: res[i].id,
                                    square: res[i].square,
                                    height: res[i].height,
                                    room_type: res[i].room_type,
                                    is_filled: "Fixed",
                                    furniture:[]
                                })
                            }else{
                                row.rooms.push({
                                    id: res[i].id,
                                    square: res[i].square,
                                    height: res[i].height,
                                    room_type: res[i].room_type,
                                    is_filled: "Fixing",
                                    furniture:[]
                                })
                            }
                        }
                        setGetroom(true);
                    }
                }
            }
        );
    }
    useEffect(()=>{
        if(!getroom){
            getRoom();
        }
    });
    return (
        <React.Fragment>
            <TableRow className={classes.root}>
                <TableCell>
                    <IconButton aria-label="expand row" size="small" onClick={() => setOpen(!open)}>
                        {open ? <KeyboardArrowUpIcon /> : <KeyboardArrowDownIcon />}
