function CollapsibleTable() {
    const [gethouse,setGethouse] = useState(false);
    function getHouses(){
        $.ajax({
                url: "api/houses",
                method:"GET",
                headers: {
                    'Content-Type': 'application/json', // 设置内容类型为 JSON
                    'token': window.sessionStorage.getItem("token") // 设置授权头
                    // 可以添加其他自定义请求头
                },
                async:false,
                success:function (res){
                    if(res.length > 0){
                        rows.length = 0;
                        for(var i in res){
                            rows.push({
                                id:res[i].id,
                                street:res[i].address.street,
                                country:res[i].address.country,
                                city:res[i].address.city,
                                house_type:res[i].house_type,
                                rooms:[]
                            })
                        }
                        setGethouse(true);
                    }
                }
            }
        );
    }
    useEffect(()=>{
        if(!gethouse){
            getHouses()
        }
    });
    return (
        <TableContainer component={Paper}>
            <Table aria-label="collapsible table">
                <TableHead>
                    <TableRow>
                        <TableCell />
                        <TableCell><Typography variant={"h4"}>Id</Typography></TableCell>
                        <TableCell align="right"><Typography variant={"h4"}>Country</Typography></TableCell>
                        <TableCell align="right"><Typography variant={"h4"}>City</Typography></TableCell>
                        <TableCell align="right"><Typography variant={"h4"}>Street</Typography></TableCell>
                        <TableCell align="right"><Typography variant={"h4"}>House type</Typography></TableCell>
                    </TableRow>
                </TableHead>
                <TableBody>
                    {rows.map((row)=>(
                        <Row row={row} />
                    ))}
                </TableBody>
            </Table>
        </TableContainer>
    );
}

export default function MainHouse(){
    return(
        <CollapsibleTable/>
